                updateVersions: function () {
                    this.devices.forEach(function (device, index) {
                        var affiliatePromises = this.affiliates.map(function (affiliate) {
                            return this.jsonp(
                                "https://api.kobobooks.com/1.0/UpgradeCheck/Device/" +
                                device.id +
                                "/" + affiliate + "/0.0/N").then(this.parseUpdateInfo.bind(
                                self, device, affiliate));
                        }.bind(this));

                        Promise.all(affiliatePromises).then(this.greatestUpdateInfo.bind(this)).then(
                            function (info) {
                                this.devices[index] = info;
                                window.setTimeout(function () {
                                    this.$forceUpdate();
                                }.bind(this), 500);
                            }.bind(this)).catch(function (err) {
                            console.warn("Error getting update info: ", affiliate, device.id,
                                device.model, err)
                            var info = device;
                            info.version = "Error loading info";
                            info.date = "";
                            info.download = "";
                            info.notes = "";
                            info.otherversions = [];
                            this.devices[index] = info;
                            window.setTimeout(function () {
                                this.$forceUpdate();
                            }.bind(this), 500);
                        }.bind(this));
                    }.bind(this))
                },
                jsonp: function jsonp(url) {
                    var CALLBACK_CHARS = 'abcdefghijklmnopqrstuvwxyz0123456789';
                    var timeout = 30000;
                    var jsonpurl = "https://json2jsonp.com/";

                    return new Promise(function (resolve, reject) {
                        var callback;
                        while (!callback || window[callback] !== undefined) {
                            callback = 'JSONP_';
                            for (var i = 0; i < 10; i++) {
                                callback += CALLBACK_CHARS[Math.random() * CALLBACK_CHARS.length |
                                    0];
                            }
                        }

                        function cleanup() {
                            delete window[callback];
                            script.remove();
                        }

                        var ticket = setTimeout(function () {
                            cleanup();
                            reject("Connection timed out");
                        }, timeout);

                        window[callback] = function (data) {
                            clearTimeout(ticket);
                            cleanup();
                            resolve({
                                url: url,
                                data: data
                            });
                        };

                        var script = document.createElement('script');
                        script.src = jsonpurl + '?url=' + encodeURIComponent(url) +
                            '&callback=' +
                            encodeURIComponent(callback);
                        document.body.appendChild(script);
                    });
                },
                fixDate: function (date, version) {
                    var newDate = date;
                    newDate = newDate && newDate[0].toUpperCase() + newDate.slice(1);
                    newDate = [newDate.slice(0, newDate.search(/\d/)), " ", newDate.slice(newDate.search(
                        /\d/))].join('');
                    if (version == "4.6.9960") {
                        newDate = "October 2017";
                    }
                    if (version == "4.6.10075") {
                        newDate = "November 2017";
                    }
                    newDate = newDate.replace("Oct 2017", "October 2017");
                    newDate = newDate.replace("Dec 2017", "December 2017");
                    return newDate;
                },
                parseUpdateInfo: function (device, affiliate, info) {
                    var r = {};
                    try {
                        r = {
                            id: device.id,
                            image: device.image,
                            model: device.model,
                            hardware: device.hardware,
                            affiliate: affiliate,
                            version: info.data.UpgradeURL.replace('kobo-update-', '').replace('.zip',
                                '').split('/').pop().replace("AuraONE_", "").replace("kobo-limitededitionupdate-", ""),
                            date: this.fixDate(info.data.UpgradeURL.split("/")[5], info.data.UpgradeURL.replace('kobo-update-', '').replace(
                                '.zip',
                                '').split('/').pop().replace("AuraONE_", "")),
                            upgradeType: info.data.UpgradeType,
                            download: info.data.UpgradeURL,
                            notes: info.data.ReleaseNoteURL,
                            otherversions: device.otherversions || []
                        };
                        r.otherversions.push({
                            affiliate: affiliate,
                            version: info.data.UpgradeURL.replace('kobo-update-', '').replace(
                                '.zip',
                                '').split('/').pop().replace("AuraONE_", ""),
                            date: this.fixDate(info.data.UpgradeURL.split("/")[5], info.data.UpgradeURL.replace('kobo-update-', '').replace(
                                '.zip',
                                '').split('/').pop().replace("AuraONE_", "")),
                            upgradeType: info.data.UpgradeType,
                            download: info.data.UpgradeURL,
                            notes: info.data.ReleaseNoteURL
                        });
                        return Promise.resolve(r);
                    } catch (e) {
                        console.warn(e);
                        return Promise.reject("Error parsing update info");
                    }
                },
                greatestUpdateInfo: function (updateInfos) {
                    return new Promise(function (resolve, reject) {
                        var versions = updateInfos.map(function (i) {
                            return i.version;
                        });
                        var greatestVersion = function (versions) {
                            return new Promise(function (resolve, reject) {
                                var versionCompare = function (v1, v2, options) {
                                    var lexicographical = options && options.lexicographical,
                                        zeroExtend = options && options.zeroExtend,
                                        v1parts = v1.split('.'),
                                        v2parts = v2.split('.');

                                    function isValidPart(x) {
                                        return (lexicographical ? /^\d+[A-Za-z]*$/ :
                                            /^\d+$/).test(
                                            x);
                                    }

                                    if (!v1parts.every(isValidPart) || !v2parts.every(
                                            isValidPart)) {
                                        return NaN;
                                    }

                                    if (zeroExtend) {
                                        while (v1parts.length < v2parts.length)
                                            v1parts.push(
                                                "0");
                                        while (v2parts.length < v1parts.length)
                                            v2parts.push(
                                                "0");
                                    }

                                    if (!lexicographical) {
                                        v1parts = v1parts.map(Number);
                                        v2parts = v2parts.map(Number);
                                    }

                                    for (var i = 0; i < v1parts.length; ++i) {
                                        if (v2parts.length == i) {
                                            return 1;
                                        }

                                        if (v1parts[i] == v2parts[i]) {
                                            continue;
                                        } else if (v1parts[i] > v2parts[i]) {
                                            return 1;
                                        } else {
                                            return -1;
                                        }
                                    }

                                    if (v1parts.length != v2parts.length) {
                                        return -1;
                                    }

                                    return 0;
                                }

                                if (versions.length < 1) {
                                    reject("No versions specified")
                                    return;
                                }

                                var currentVersion = versions[0];
                                for (var i = 0; i < versions.length; i++) {
                                    currentVersion = versionCompare(currentVersion,
                                            versions[i], {
                                                lexicographical: false,
                                                zeroExtend: false
                                            }) == 1 ? currentVersion :
                                        versions[i];
                                }

                                resolve(currentVersion);
                            });
                        };
                        greatestVersion(versions).then(function (r) {
                            var info = updateInfos[versions.indexOf(r)];
                            info.otherversions = JSON.parse(JSON.stringify(updateInfos));
                            resolve(info);
                        }).catch(reject);
                    });
                },
